geoCommand.object:
DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_USER' AND pr.INSERT_PERMITTED = '1';
SELECT COUNT(*) + v_permission_granted INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_ADMIN' AND pr.INSERT_PERMITTED = '1';
IF v_permission_granted > 0 THEN BEGIN INSERT INTO WEB50.LIB_COVERAGE(GID, WKB_GEOMETRY,"XMIN","XMAX",YMIN,YMAX,CTIME,CUSER_ID) VALUES(:ID, :WKB, :XMIN, :XMAX, :YMIN, :YMAX,SYSDATE,:USER_ID);
UPDATE WEB50.LIB_DOC SET GID = :ID WHERE DOC_ID = :ID; END; ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на добавление объектов. Обратитесь к администратору.'); END IF; END;

DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_USER' AND pr.UPDATE_PERMITTED = '1' AND EXISTS (SELECT 1 FROM WEB50.LIB_DOC t WHERE t.doc_id = :ID AND (t.status_id = 1 OR (t.cuser_id = :USER_ID AND t.status_id = 0 )));
SELECT COUNT(*) + v_permission_granted INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_ADMIN' AND pr.UPDATE_PERMITTED = '1';
IF v_permission_granted > 0 THEN BEGIN UPDATE WEB50.LIB_COVERAGE SET WKB_GEOMETRY=:WKB ,"XMIN"=:XMIN ,"XMAX"=:XMAX ,YMIN=:YMIN ,YMAX=:YMAX ,MTIME=SYSDATE ,MUSER_ID=:USER_ID WHERE GID = :ID; END; ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на редактирование объектов. Обратитесь к администратору.'); END IF; END;

dbCommand.query:
DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_USER' AND pr.DELETE_PERMITTED = '1' AND EXISTS (SELECT 1 FROM WEB50.LIB_DOC t WHERE t.doc_id = :ID AND (t.status_id = 1 OR (t.cuser_id = :USER_ID AND t.status_id = 0 )));
SELECT COUNT(*) + v_permission_granted INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_LIB_DOC_ADMIN' AND pr.DELETE_PERMITTED = '1';
IF v_permission_granted > 0 THEN BEGIN UPDATE WEB50.LIB_DOC SET GID = NULL ,MTIME=SYSDATE ,MUSER_ID=:USER_ID WHERE DOC_ID = :ID;
DELETE FROM WEB50.LIB_COVERAGE WHERE GID = :ID; END; ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на удаление объектов. Обратитесь к администратору.'); END IF; END;

