dbQuery.query:
SELECT * FROM (SELECT ZONE_ID ID,ZONE_ID, DESCR, CTIME, CUSER, MTIME, MUSER_ID, CUSER_ID, MUSER
FROM WEB50.M_1_ZONE) d
WHERE {FILTER}

dbCommand.query:
DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_M1_MAP' AND pr.INSERT_PERMITTED = 1; IF v_permission_granted > 0 THEN
  INSERT INTO WEB50.M_1_ZONE(ZONE_ID,ID,DESCR,CUSER_ID)
  VALUES(:ZONE_ID,:ZONE_ID,:DESCR,:USER_ID)
  RETURNING ZONE_ID INTO :ID;
ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на добавление объектов. Обратитесь к администратору.'); END IF; END;

DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_M1_MAP' AND pr.UPDATE_PERMITTED = 1; IF v_permission_granted > 0 THEN
  UPDATE WEB50.M_1_ZONE
  SET DESCR=:DESCR,MUSER_ID=:USER_ID 
  WHERE ZONE_ID = :ID;
ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на редактирование объектов. Обратитесь к администратору.'); END IF; END;

                  DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r JOIN WEB50.B_ROLE_TO_B_POLICY r2p ON u2r.ROLE_ID = r2p.ROLE_ID JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE u2r.USER_ID = :USER_ID AND pr.CLASS_ID='WEB50_M1_MAP' AND pr.DELETE_PERMITTED = 1; IF v_permission_granted > 0 THEN
				    DELETE FROM WEB50.M_1_ZONE_TO_SRV_DISTRICT
                    WHERE ZONE_ID = :ID;
                    DELETE FROM WEB50.M_1_ZONE
                    WHERE ZONE_ID = :ID;
                  ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на удаление объектов. Обратитесь к администратору.'); END IF; END;
