DECLARE v_permission_granted NUMBER; BEGIN SELECT COUNT(*) INTO v_permission_granted FROM WEB50.B_USER_TO_B_ROLE u2r WHERE u2r.USER_ID = :USER_ID AND ((EXISTS (SELECT 1 FROM WEB50.B_ROLE_TO_B_POLICY r2p JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE r2p.role_id = u2r.role_id AND pr.CLASS_ID='PODS_DATA' AND pr.INSERT_PERMITTED = 1) AND NOT EXISTS (SELECT 1 FROM WEB50.B_ROLE_TO_B_POLICY r2p JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE r2p.role_id = u2r.role_id AND pr.CLASS_ID='PODS_ACTIVITY_ZONE_CL')) OR EXISTS (SELECT 1 FROM WEB50.B_ROLE_TO_B_POLICY r2p JOIN WEB50.B_POLICY_RULES pr ON r2p.POLICY_ID = pr.POLICY_ID WHERE r2p.role_id = u2r.role_id AND pr.CLASS_ID='PODS_ACTIVITY_ZONE_CL' AND pr.INSERT_PERMITTED = 1)); IF v_permission_granted > 0 THEN NULL;
BEGIN INSERT INTO PODS.ACTIVITY_ZONE_CL(CODE, DESCRIPTION, ACTIVE_INDICATOR_LF, SOURCE_GCL) VALUES ('{CODE}', '{DESCR}', '{ACTIVE_INDICATOR_LF}', '{SOURCE_GCL}'); :ID := '{CODE}'; END;
ELSE RAISE_APPLICATION_ERROR(-20771,'Нет прав на добавление объектов. Обратитесь к администратору.'); END IF; END;

