IF v_permission_granted > 0 THEN NULL;
BEGIN UPDATE PODS.ILI_INSPECTION SET BEGIN_DATE = to_date('{BEGIN_DATE}', 'DD.MM.YYYY') ,END_DATE = to_date('{END_DATE}', 'DD.MM.YYYY') ,START_ODOMETER = '{START_ODOMETER}' ,END_ODOMETER = '{END_ODOMETER}' ,TOOL_TYPE_CL = '{TOOL_TYPE_CL}' ,TOOL_VENDOR_CL = '{TOOL_VENDOR_CL}' ,MODEL = '{MODEL}' ,SENSOR_GCL = '{SENSOR_GCL}' ,SENSOR_SPACING_MIN = '{SENSOR_SPACING_MIN}' ,SENSOR_SPACING_MAX = '{SENSOR_SPACING_MAX}' ,MIN_TEMP = '{MIN_TEMP}' ,MAX_TEMP = '{MAX_TEMP}' ,AVG_TEMP = '{AVG_TEMP}' ,MIN_VELOCITY = '{MIN_VELOCITY}' ,MAX_VELOCITY = '{MAX_VELOCITY}' ,AVG_VELOCITY = '{AVG_VELOCITY}' ,SAMPLING_FREQUENCY = '{SAMPLING_FREQUENCY}' ,RESOLUTION = '{RESOLUTION}' ,RATED_MAX_VELOCITY = '{RATED_MAX_VELOCITY}' ,RATED_MAX_WT = '{RATED_MAX_WT}' ,CLUSTER_RULE_CL = '{CLUSTER_RULE_CL}' ,SOURCE_GCL = '{SOURCE_GCL}' ,COMMENTS = '{COMMENTS}' WHERE ILI_INSPECTION_ID = '{ILI_INSPECTION_ID}'; END;
